DROP DATABASE IF EXISTS SWP391_SE1632_G2;

CREATE DATABASE SWP391_SE1632_G2;

USE SWP391_SE1632_G2;

CREATE TABLE Account (
	accountID int auto_increment not null,
	userName varchar(150) not null unique,
    password varchar(100) not null,
    displayName nvarchar(256) null,
    email varchar(256) not null unique,
    avatar varchar(150) null,
    isBlock boolean not null,
    status int not null,
    createDate TIMESTAMP not null,
    rid int not null,
    PRIMARY KEY(accountID)
);

CREATE TABLE Role (
	rid int auto_increment not null,
	rname nvarchar(255) not null unique,
	primary key (rid)
);

CREATE TABLE Major (
	majorId int auto_increment not null,
	majorName_En varchar(255) not null unique,
	majorName_Vi nvarchar(255) not null unique,
	keyword nvarchar(100) not null unique,
	image varchar(150) null,
	PRIMARY KEY (majorID)
);

CREATE TABLE Decision (
	decisionNo nvarchar(150) not null,
    decisionName nvarchar(256) not null,
    approveDate date not null ,
    note nvarchar(150) not null,
    createDate date not null ,
    isActive boolean not null,
    fileName varchar(250) null,
    PRIMARY KEY (decisionNo)
);

CREATE TABLE Curriculum (
	curid int auto_increment not null,
	curCode nvarchar(255) not null unique,
	majorID int not null,
    decisionNo nvarchar(150) not null,
	curName_EN varchar(255) not null,
	curName_VI nvarchar(255) not null,
	description nvarchar(3000) not null,
	isApproved boolean not null,
    isActive boolean not null,
	image varchar(150) null,
	PRIMARY KEY (curid)
);

CREATE TABLE Student (
	stid varchar(150) not null,
	userName varchar(255) not null unique,
	curid int not null,
	semester nvarchar(100) not null,
	isActive boolean not null,
	PRIMARY KEY (stid)
);

CREATE TABLE Lecturer (
	lid int auto_increment not null,
	userName varchar(255) not null unique,
	PRIMARY KEY (lid)
);

CREATE TABLE Class (
	cid int auto_increment not null,
	cname varchar(150) not null,
	lid int not null,
	subjectCode varchar(255) not null,
	year year null,
	startDate date ,
	endDate date null,
	PRIMARY KEY (cid)
);

CREATE TABLE Subject (
	subjectCode varchar(255) not null,
	subjectName_En varchar(255) not null ,
	subjectName_Vi nvarchar(255) not null ,
	semester varchar(100) not null,
	noCredit int not null,
	note nvarchar(3000) null,
	isCombo boolean not null,
	comboName varchar(255) null,
	isElective boolean not null,
	electiveName varchar(255) null,
	createDate date ,
    isActive boolean not null,
	PRIMARY KEY (subjectCode)
);

CREATE TABLE PreRequisite (
	preID int auto_increment not null,
    slbid int,
	subjectCode varchar(255),
	preReqCode varchar(3000) not null,
	PRIMARY KEY (preID)
);

CREATE TABLE Session (
	sesid int auto_increment not null,
	slbid int,
	sesNo int not null,
	topic nvarchar(255) not null,
	learning_Teaching_Type nvarchar(150) null,
	ITU varchar(150) null,
	StudentMaterial nvarchar(255) null,
	dowload varchar(255) null,
	StudentTask nvarchar(255) null,
	urls varchar(255) null,
	note nvarchar(255) null,
    isActive boolean not null,
    accountID int not null,
	PRIMARY KEY (sesid)
);

CREATE TABLE Elective (
	eid int auto_increment not null,
	ename nvarchar(255) not null unique,
	tag nvarchar(255) not null unique,
	subjectName_EN varchar(255) not null,
	subjectName_VI nvarchar(255) not null,
	note nvarchar(3000) null,
	createDate date ,
    isActive boolean not null,
	PRIMARY KEY (eid)
);

CREATE TABLE Combo (
	cid int auto_increment not null,
	tag varchar(255) not null unique,
	comboName_EN varchar(255) null ,
	comboName_VI nvarchar(255) null ,
	note nvarchar(3000) null,
	createDate date ,
    isActive boolean not null,
	PRIMARY KEY (cid)
);


CREATE TABLE Room (
	rid int auto_increment not null,
	rname varchar(150) not null unique,
	PRIMARY KEY (rid)
);

CREATE TABLE PO (
	poid int auto_increment not null,
	/*curid int not null,*/
	poName varchar(150) not null,
	poDescription varchar(3000) not null,
    isActive boolean not null,
	PRIMARY KEY (poid)
);

CREATE TABLE PLO_Curriculum (
	curid int not null,
    ploid int not null,
    PRIMARY KEY (curid, ploid)
);

CREATE TABLE PO_Curriculum (
	curid int not null,
    poid int not null,
    PRIMARY KEY (curid, poid)
);

CREATE TABLE PLO (
	ploid int auto_increment not null,
	/*curid int not null,*/ 
	ploName nvarchar(150) not null,
	ploDescription nvarchar(3000) not null,
    isActive boolean not null,
	PRIMARY KEY (ploid)
);

CREATE TABLE LO (
	loid int auto_increment not null,
    slbid int not null,
    cloName nvarchar(256) not null,
    cloDetail nvarchar(300)  null,
    loDetail nvarchar(3000)  null,
    isActive bit,
    primary key (loid)
);


CREATE TABLE Subject_Mapping_PLO (
	subjectCode varchar(255) not null,
    ploid int not null,
    isMapping bit,
    primary key (subjectCode, ploid)
);

CREATE TABLE Job (
	jid int auto_increment not null,
	content nvarchar(3000) not null,
    isActive boolean not null,
    PRIMARY KEY (jid)
);

CREATE TABLE Question (
	qid int auto_increment not null,
	sesid int not null,
	qname nvarchar(3000) not null,
	details nvarchar(3000) not null,
	answer nvarchar(3000) null,
    isActive boolean not null,
    accountID int not null,
	PRIMARY KEY(qid)
);

CREATE TABLE Syllabus (
	slbid int auto_increment not null,
    accountID int not null default 1,
	subjectCode varchar(255) not null,
    decisionNo nvarchar(150) not null, 
	slbName_EN varchar(255) not null,
	slbName_VI nvarchar(255) not null,
	degreeLevel varchar(255) not null,
	timeAllocation nvarchar(1000) not null,
	description nvarchar(7000) not null,
	studentTask varchar(3000) not null,
	tool varchar(500) not null,
	isApproved boolean not null,
    isActive boolean not null,
	approvedDate date null,
	note varchar(1000) null,
	MinAvgMarkToPass int,
	PRIMARY KEY (slbid)
);

CREATE TABLE Material (
	mid int auto_increment not null,
	slbid int not null,
	description nvarchar(3000) not null,
	author nvarchar(3000)  null,
	publisher nvarchar(3000)  null,
	publishedDate date  null,
	edition nvarchar(3000)  null,
	ISBN nvarchar(3000),
	isMainMaterial boolean not null,
	isHardCopy boolean not null,
	isOnline boolean not null,
	note nvarchar(3000) null,
    isActive boolean not null,
    accountID int not null,
	PRIMARY KEY (mid)
);

CREATE TABLE Assessment (
	assid int auto_increment not null,
	slbid int not null,
	category varchar(255) not null,
	type nvarchar(150) not null,
	part int not null,
	weight float not null,
	completionCriteria varchar(50) not null,
	duration nvarchar(3000) not null,
	questionType nvarchar(3000) not null,
	noQuestion nvarchar(3000) not null,
	knowledge_Skill nvarchar(3000) not null,
	gradingGuide nvarchar(3000) not null,
	note nvarchar(3000) null,
    isAcitve boolean not null,
    accountId int not null,
	PRIMARY KEY (assid)
);

CREATE TABLE AssessmentDetail (
	assdeID int auto_increment not null,
	assID int not null,
	assessmentItem nvarchar(255) not null,
	isMain boolean not null,
	weight float not null,
	primary key (assdeID)
);


